import { tags } from "typia";

/**
 * Response for a successful password change request.
 *
 * Contains only safe metadata for UX and audit surfaces. No password or hash
 * material is ever exposed. Operational details may include whether other
 * sessions were revoked.
 *
 * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
 */
export type ITodoAppPasswordChange = {
  /** Indicates whether the password change completed successfully. */
  success: boolean;

  /**
   * Server timestamp when the password change took effect (ISO 8601,
   * timestamptz).
   */
  changedAt: string & tags.Format<"date-time">;

  /**
   * True if other active sessions were revoked as part of the operation.
   *
   * Operationally ties to updates on todo_app_sessions.revoked_at and inserts
   * into todo_app_session_revocations.
   */
  revokedOtherSessions?: boolean | undefined;

  /**
   * Optional human-readable message about post-change actions or next steps.
   *
   * No secrets or hashes are ever included.
   */
  message?: string | undefined;
};
